# -*- conding = utf-8 -*-import unittestfrom util.RequestUtil import RequestUtilhost = 'https://api.xdclass.net'class IndexTestCase(unittest.TestCase):    def testIndexCategoryList(self):        """        首页分类列表        """        request = RequestUtil()        url = host + '/pub/api/v1/web/all_category'        response = request.request(url, 'get')        self.assertTrue(len(response['data']) > 0, '分类列表为空' )        self.assertEqual(response['code'], 0 , '业务状态不正常')    def testIndexVideoCard(self):        """        首页视频卡片        """        request = RequestUtil()        url = host + '/pub/api/v1/web/index_card'        response = request.request(url, 'get')        self.assertTrue(len(response['data']) > 0, '视频卡片为空')        self.assertEqual(response['code'], 0, '业务状态不正常')        video_card_list = response['data']        for card in video_card_list:            titles = card['title']            # print(titles)            print(titles)            if '大数据工程师' in titles:                print('验证通过的标题为：{0}'.format(titles))if __name__ == '__main__':    unittest.main(verbosity=2)