from selenium import webdriverfrom selenium.webdriver.support.wait import WebDriverWaitfrom selenium.webdriver.support import expected_conditions as ECfrom po import settings'''基础类封装'''class BasePage(object):    #初始化    def __init__(self, driver, url):        self._driver = driver        self._url = url     #打开浏览器    def open(self):        self._driver.get(url= self._url)    #关闭浏览器    def close(self):        self._driver.quit()    #定位元素    def find_element(self, *locator, timeout=None):        try:            self._init_wait(timeout).until(EC.visibility_of_element_located(locator=locator))        except Exception as e:            self._driver.quit()            print('定位元素失败{0}'.format(e))    #输入内容    def send_keys(self, webElement, keys):        webElement.clear()        webElement.send_keys(keys)    #点击    def click(self, locator):        ele = self.find_element(locator= locator)        ele.click()    #等待时间    def _init_wait(self, timeout):        if timeout is None:            return WebDriverWait(driver = self._driver, timeout=settings.UI_WAIT_TIME)        else:            return WebDriverWait(driver = self._driver, timeout=timeout)