import loggingclass LogUtil:    def getlog(self, filename):        #创建日志        logger = logging.getLogger('logger')        if not logger.handlers:            #设置日志等级            logger.setLevel(logging.INFO)            #输出的控制台            SH = logging.StreamHandler()            #输入到文件            FH = logging.FileHandler(filename, encoding = 'utf-8')            #日志信息格式            formatter = logging.Formatter(fmt="[%(asctime)s] [%(filename)s] %(levelname)s :%(message)s",datefmt = '%Y/%m/%d %H:%M:%S')            #添加日志格式            logger.addHandler(SH)            logger.addHandler(FH)            SH.setFormatter(formatter)            FH.setFormatter(formatter)        return logger