import requestsimport osos.environ['NO_PROXY'] = 'stackoverflow.com'class RequestUtil:    def __init__(self):        pass    def request(self, url, method, headers=None, param=None, content_type=None):        """        通用请求工具类封装        """        try:            if method == 'get':                result = requests.get(url, params=param, headers=headers).json()                return result            elif method == 'post':                if content_type == 'application/json':                    result = requests.post(url=url, json=param, headers=headers).json()                    return result                else:                    result = requests.post(url=url, data=param, headers=headers).json()                    return result            else:                print('http method not allowed!')        except Exception as e:            print('http请求报错：{0}'.format(e))if __name__ == '__main__':    # url = 'https://api.xdclass.net/pub/api/v1/web/all_category'    url = 'https://api.xdclass.net/pub/api/v1/web/web_login'    r = RequestUtil()    data = {'phone':'13311796165', 'pwd':'Tyl628919'}    headers = {'Content-Type':'application/x-www-form-urlencoded'}    # result = r.request(url, 'get')    result = r.request(url, 'post', param=data, headers=headers)    print(result)