import pymysqlconn = pymysql.connect(        host='127.0.0.1',        user='root',        password='root123456',        database='xd_test_test')#游标 cursor 方法获取操作游标，得到一个可以执行sql语句，并且操作结果作为字典返回的游标cursor = conn.cursor(pymysql.cursors.DictCursor)try:    #使用execute方法进行数据库查询    cursor.execute("select * from `case`")    data = cursor.fetchall()    print(data)except pymysql.Error as e:    print(e)finally:    #关闭连接    conn.close()